<?php
/**
 * Rollbacker.
 *
 * @package RSFV
 */

namespace RSFV\Featuresets\Rollback;

if ( ! defined( 'ABSPATH' ) ) {
	exit; // Exit if accessed directly.
}

/**
 * Rollback.
 *
 * Rollback handler class is responsible for rolling back to
 * previous version.
 *
 * @since 0.50.0
 */
class Rollbacker {

	/**
	 * Package URL.
	 *
	 * Holds the package URL.
	 *
	 * @access protected
	 *
	 * @var string Package URL.
	 */
	protected $package_url;

	/**
	 * Version.
	 *
	 * Holds the version.
	 *
	 * @access protected
	 *
	 * @var string Package URL.
	 */
	protected $version;

	/**
	 * Plugin name.
	 *
	 * Holds the plugin name.
	 *
	 * @access protected
	 *
	 * @var string Plugin name.
	 */
	protected $plugin_name;

	/**
	 * Plugin slug.
	 *
	 * Holds the plugin slug.
	 *
	 * @access protected
	 *
	 * @var string Plugin slug.
	 */
	protected $plugin_slug;

	/**
	 * Rollback constructor.
	 *
	 * Initializing rollback.
	 *
	 * @access public
	 *
	 * @param array $args Optional. Rollback arguments. Default is an empty array.
	 */
	public function __construct( $args = array() ) {
		foreach ( $args as $key => $value ) {
			$this->{$key} = $value;
		}
	}

	/**
	 * Print inline style.
	 *
	 * Add an inline CSS to the rollback page.
	 *
	 * @access private
	 */
	private function print_inline_style() {
		?>
		<style>
			.wrap {
				overflow: hidden;
				max-width: 850px;
				margin: auto;
				font-family: Courier, monospace;
			}

			h1 {
				background: #252422;
				text-align: center;
				color: #fff !important;
				padding: 70px !important;
				text-transform: uppercase;
				letter-spacing: 1px;
			}

			h1 img {
				max-width: 300px;
				display: block;
				margin: auto auto 50px;
			}

			.wrap h1 {
				position: relative;
				padding-top: 140px !important;
			}

			.wrap h1:before {
				content: '';
				position: absolute;
				width: 340px;
				height: 104px;
				color: #fff;
				top: 35px;
				background-image: url("data:image/svg+xml;base64,");
				background-repeat: no-repeat;
				left: 25%;
			}
		</style>
		<?php
	}

	/**
	 * Upgrade.
	 *
	 * Run WordPress upgrade to rollback plugin to previous version.
	 *
	 * @access protected
	 */
	protected function upgrade() {
		if ( ! class_exists( 'WP_Upgrader_Skin' ) ) {
			require_once ABSPATH . 'wp-admin/includes/class-wp-upgrader.php';
		}

		require_once __DIR__ . '/class-rollback-downgrader-skin.php';

		$skin_args = array(
			'url'    => 'update.php?action=upgrade-plugin&plugin=' . rawurlencode( $this->plugin_name ),
			'plugin' => $this->plugin_name,
			'nonce'  => 'upgrade-plugin_' . $this->plugin_name,
			'title'  => esc_html__( 'Rollback to Previous Version', 'rsfv' ),
		);

		$this->print_inline_style();

		$skin     = new Rollback_Downgrader_Skin( $skin_args );
		$upgrader = new \Plugin_Upgrader( $skin );

		// IMPORTANT: initialize + install strings so feedback() can map keys.
		$upgrader->init();
		$upgrader->upgrade_strings();
		$upgrader->run(
			array(
				'package'                     => $this->package_url,
				'destination'                 => WP_PLUGIN_DIR,
				'abort_if_destination_exists' => false,
				'clear_working'               => true,
				'hook_extra'                  => array(
					'plugin' => $this->plugin_name,
					'type'   => 'plugin',
					'action' => 'update',
				),
			)
		);
	}

	/**
	 * Run.
	 *
	 * Rollback plugin to previous versions.
	 *
	 * @access public
	 */
	public function run() {
		$this->upgrade();
	}
}
